function getGETtext(u,callback){
    fetch(u).then(function(response){return response.text()})
    .then(function(tx) {
    	//console.log('getGETtex=',tx);
    	callback(tx);
    }).catch(function(e){console.log("ERR:",String(e.name),String(e.message));})	
}

function getGETjson(u,callback){
    fetch(u).then(function(response){return response.json()})
    .then(function(json) {
    	//console.log('getGETtex=',json);
    	callback(json);
    }).catch(function(e){console.log("ERR:",String(e.name),String(e.message));})	
}

