function upCase(tx){return tx.charAt(0).toUpperCase() + tx.slice(1);}

function dayofweek(date) {
    var weekdays = ["Niedziela", "Poniedziałek", "Wtorek", "Środa", "Czwartek", "Piątek", "Sobota"];
    var d = new Date();    
	if (date) {
		var arr = date.split('-');
		arr.reverse();
		date = arr.join("-");
		d.setTime(Date.parse(date)) ;
	}
    var dw = d.getDay();
    return weekdays[dw];
}

function parseMessage(m){
    var room = m.match(/room\=(.*?),/);
    var date = m.match(/dateFrom\=(.*?),/);
    //node.warn(room[1]+' '+date[1]);
    return room[1]+', '+date[1];
}

if (msg.payload.code == "404"){
    msg.status  = msg.payload.error;
    msg.message = parseMessage(msg.payload.message);
    return msg;
}


var payload = msg.payload;

msg.payload = payload[0].lessons;

msg.info = {}
msg.info.date = payload[0].date;
msg.info.dw = dayofweek(msg.info.date);
//room: "WI WI1- 215"
msg.info.room                = payload[0].lessons[0].room.replace("WI ","").replace(" ","");
msg.info.faculty             = payload[0].lessons[0].faculty;
msg.info.facultyAbbreviation = payload[0].lessons[0].facultyAbbreviation;
msg.info.fieldOfStudy = upCase(payload[0].lessons[0].fieldOfStudy);

msg.payload.forEach(function(p,i){
    msg.payload[i].group                = msg.payload[i].group.replace(/_/g,"-");
    msg.payload[i].courseType    = upCase(msg.payload[i].courseType);
    msg.payload[i].fieldOfStudy  = upCase(msg.payload[i].fieldOfStudy);

})


return msg;

function pads(s) { return ("00"+s.toString()).slice(-3); }
function today(delta) {
	function pad(s) { return ("0"+s.toString()).slice(-2); }
	var d = new Date();
	if (delta) d = new Date(d.getTime()+(delta * 3600*1000*24));
	var rok     = d.getFullYear().toString();
	var miesiac = pad(d.getMonth()+1);
	var dzien   = pad(d.getDate());
	return dzien+'-'+miesiac+'-'+rok;
}

